mapper.select:
SELECT IFNULL(g.id,"") as id,IFNULL(g.name,"") as name,IFNULL(g.coverImg,"") as coverImg,IFNULL(g.coverImg,"") as coverImg,IFNULL(s.`name`,"") as spec,IFNULL(s.price,"") as price
FROM sys_good g LEFT JOIN sys_spec s on g.id = s.gid WHERE g.cid = 1 and g.isdel = 0 and s.isdel = 0 and s.`order` = 1

  AND isNew = #{isnew,jdbcType=INTEGER}


  AND isRecommend = #{isrecommend,jdbcType=INTEGER}


  AND isSelected = #{isselected,jdbcType=INTEGER}

SELECT g.id, g.name, IFNULL(g.goodno,"") as goodno, IFNULL(g.coverImg,"") as coverImg, c.`name` as categoryName FROM sys_good g INNER JOIN sys_category c on g.categoryId = c.id WHERE g.isdel = 0 AND c.isdel = 0 AND g.cid = #{cid,jdbcType=VARCHAR}
AND c.`name` LIKE CONCAT(CONCAT('%', #{search}), '%')
AND g.categoryId = #{categoryId,jdbcType=VARCHAR}
limit #{start,jdbcType=INTEGER},#{num,jdbcType=INTEGER}

SELECT count(1) FROM sys_good g INNER JOIN sys_category c on g.categoryId = c.id WHERE g.isdel = 0 AND c.isdel = 0 AND g.cid = #{cid,jdbcType=VARCHAR}
AND c.`name` LIKE CONCAT(CONCAT('%', #{search}), '%')
AND g.categoryId = #{categoryId,jdbcType=VARCHAR}
limit #{start,jdbcType=INTEGER},#{num,jdbcType=INTEGER}
