mapper.select:
SELECT id, orderNo, totalPrice, IFNULL(payNo,"") as payNo, payPrice, paytype, orderstatus, IFNULL(DATE_FORMAT(create_time,"%Y-%m-%d %H:%i:%S"),"") as createTime FROM sys_order WHERE isdel = 0 AND cid = #{cid,jdbcType=VARCHAR}
AND orderstatus = #{orderstatus,jdbcType=INTEGER}
AND create_time BETWEEN #{startTime,jdbcType=VARCHAR} AND #{endTime,jdbcType=VARCHAR}
AND orderNo LIKE CONCAT(CONCAT('%', #{search}), '%')
limit #{start,jdbcType=INTEGER},#{num,jdbcType=INTEGER}

SELECT
count(1)
FROM
sys_order
WHERE
isdel = 0
AND cid = #{cid,jdbcType=VARCHAR}

  AND  orderstatus = #{orderstatus,jdbcType=INTEGER}


  AND  create_time BETWEEN #{startTime,jdbcType=VARCHAR} AND #{endTime,jdbcType=VARCHAR}


  AND orderNo LIKE CONCAT(CONCAT('%', #{search}), '%')


  limit #{start,jdbcType=INTEGER},#{num,jdbcType=INTEGER}

SELECT
id,
orderNo AS number,
totalPrice AS money,
orderstatus AS  status,
IFNULL(DATE_FORMAT(create_time,"%Y-%m-%d %H:%i:%S"),"") as createTime
from sys_order
WHERE  isDel = 0
AND cid = #{cid,jdbcType=VARCHAR}
AND create_by = #{createBy,jdbcType=VARCHAR}

  AND  orderstatus = #{orderstatus,jdbcType=INTEGER}


  limit #{start,jdbcType=INTEGER},#{num,jdbcType=INTEGER}
