mapper.select:
SELECT
    IFNULL(SUM(num),0) as num
FROM
    sys_shopping_cart
WHERE
    gid = #{gid,jdbcType=BIGINT}
AND isdel = 0
AND cid = #{cid,jdbcType=BIGINT}
AND create_by = #{createBy,jdbcType=VARCHAR}

mapper.update:
update sys_shopping_cart


        num = num+${num},


        update_by = #{updateBy,jdbcType=VARCHAR},


        update_time = #{updateTime,jdbcType=TIMESTAMP},


where
gid = #{gid,jdbcType=BIGINT}
AND isdel = 0
AND cid = #{cid,jdbcType=BIGINT}
AND create_by = #{createBy,jdbcType=VARCHAR}
AND num = ${version}

mapper.insert:
insert into sys_shopping_cart (id, gid, num, spec, 
  price, cid, isdel, create_by, 
  create_time, update_by, update_time
  )
values (#{id,jdbcType=BIGINT}, #{gid,jdbcType=BIGINT}, #{num,jdbcType=INTEGER}, #{spec,jdbcType=VARCHAR}, 
  #{price,jdbcType=DECIMAL}, #{cid,jdbcType=BIGINT}, #{isdel,jdbcType=INTEGER}, #{createBy,jdbcType=VARCHAR}, 
  #{createTime,jdbcType=TIMESTAMP}, #{updateBy,jdbcType=VARCHAR}, #{updateTime,jdbcType=TIMESTAMP}
  )
